mapper.select:
select id, dept_id, student_id, student_name, sex, phone_number, out_range, out_location, reason, start_time, end_time, duration, parents_name, parents_number, permission, creat_time from t_application
and permission = #{permission}
and creat_time like '${creatTime}%'
and student_id = #{studentId}
order by creat_time desc

select count(*)
from t_application


    and permission = #{permission}


    and creat_time like '${creatTime}%'

  and student_id = #{studentId}

mapper.update:
update t_application
set out_location = #{outLocation},out_range = #{outRange},reason = #{reason},start_time = #{startTime},end_time = #{endTime} ,duration = #{duration}
where id = #{id}

update t_application
set permission = '已过期'
where student_id = #{studentId} and DATE(start_time) < #{nowTime} and permission = '待审批';

UPDATE t_application
SET permission = #{permission}
WHERE
    id = #{id};
