SELECT t_health.id,t_health.user_id,t_health.user_name,t_health.clazz_id,t_health.clazz_name,t_health.morning_check,t_health.afternoon_check,t_health.in_school,t_health.in_risk,t_health.in_isolation,t_health.clock_time FROM t_student, t_health, t_counsellor, t_clazz WHERE t_counsellor.id = #{id} AND clock_time = #{clockTime}
AND t_student.clazz_id = #{clazzId}
AND t_student.id = t_health.user_id AND t_counsellor.id = t_clazz.counsellor_id AND t_health.clazz_id = t_clazz.id

SELECT COUNT(*) FROM t_student, t_health, t_counsellor, t_clazz WHERE t_counsellor.id = #{id} AND clock_time = #{clockTime}
AND t_student.clazz_id = #{clazzId}
AND t_student.id = t_health.user_id AND t_counsellor.id = t_clazz.counsellor_id AND t_health.clazz_id = t_clazz.id

SELECT s1.id as user_id, s1.NAME as user_name, s1.clazz_id as clazz_id, s1.clazz_name as clazz_name FROM t_student s1, t_counsellor, t_clazz WHERE t_counsellor.id = #{id}
AND s1.clazz_id = #{clazzId}
AND t_counsellor.id = t_clazz.counsellor_id AND s1.clazz_id = t_clazz.id AND s1.id NOT IN ( SELECT s2.id FROM t_student s2, t_health, t_counsellor, t_clazz WHERE t_counsellor.id = #{id} AND s2.id = t_health.user_id AND t_counsellor.id = t_clazz.counsellor_id AND t_health.clazz_id = t_clazz.id AND clock_time = #{clockTime} )

SELECT COUNT(*) FROM t_student s1, t_counsellor, t_clazz WHERE t_counsellor.id = #{id}
AND s1.clazz_id = #{clazzId}
AND t_counsellor.id = t_clazz.counsellor_id AND s1.clazz_id = t_clazz.id AND s1.id NOT IN ( SELECT s2.id FROM t_student s2, t_health, t_counsellor, t_clazz WHERE t_counsellor.id = #{id} AND s2.id = t_health.user_id AND t_counsellor.id = t_clazz.counsellor_id AND t_health.clazz_id = t_clazz.id AND clock_time = #{clockTime} )

SELECT t_health.id,t_health.user_id,t_health.user_name,t_health.clazz_id,t_health.clazz_name,t_health.morning_check,t_health.afternoon_check,t_health.in_school,t_health.in_risk,t_health.in_isolation,t_health.clock_time FROM t_student, t_health, t_dept, t_clazz
AND t_dept.id = #{deptId}
AND clock_time = #{clockTime}
AND t_student.clazz_id = #{clazzId}
t_health.morning_check = '是' or t_health.afternoon_check = '是'
t_health.in_isolation = '是'
t_health.in_risk = '是'
t_health.in_school = '否'
AND t_student.id = t_health.user_id AND t_dept.id = t_clazz.dept_id AND t_health.clazz_id = t_clazz.id

SELECT COUNT(*) FROM t_student, t_health, t_dept, t_clazz
AND t_dept.id = #{deptId}
AND clock_time = #{clockTime}
AND t_student.clazz_id = #{clazzId}
t_health.morning_check = '是' or t_health.afternoon_check = '是'
t_health.in_isolation = '是'
t_health.in_risk = '是'
t_health.in_school = '否'
AND t_student.id = t_health.user_id AND t_dept.id = t_clazz.dept_id AND t_health.clazz_id = t_clazz.id

SELECT s1.id as user_id, s1.NAME as user_name, s1.clazz_id as clazz_id, s1.clazz_name as clazz_name FROM t_student s1, t_dept, t_clazz
AND t_dept.id = #{deptId}
AND s1.clazz_id = #{clazzId}
AND t_dept.id = t_clazz.dept_id AND s1.clazz_id = t_clazz.id AND s1.NAME
NOT IN ( SELECT s2.NAME FROM t_student s2, t_health, t_dept, t_clazz
AND t_dept.id = #{deptId}
AND s2.id = t_health.user_id AND t_dept.id = t_clazz.dept_id AND t_health.clazz_id = t_clazz.id AND clock_time = #{clockTime}
)

